INSERT INTO `posts` (`id`, `author_id`, `title`, `description`, `content`, `date`) VALUES (1, 1, 'At inventore alias autem.', 'Sed corporis impedit vel delectus sit. Aut quidem omnis voluptate architecto dicta est rerum. Excepturi voluptatem ipsa nobis vel suscipit cumque. Omnis cupiditate enim quidem perspiciatis et.', 'Et odit aspernatur consectetur eveniet consequuntur architecto dolore. Sunt aut odit et eligendi unde est.', '1991-12-28');
INSERT INTO `posts` (`id`, `author_id`, `title`, `description`, `content`, `date`) VALUES (2, 2, 'Vel aut pariatur rerum earum corrupti doloremque debitis.', 'Exercitationem voluptatem ex sunt nemo. Nesciunt dolorem sit sunt deleniti perspiciatis doloremque.', 'Fuga et dignissimos laboriosam delectus. Quisquam dolorem sint delectus ut consequuntur id dolor. Exercitationem ipsum sunt impedit ut id.', '2010-12-12');
INSERT INTO `posts` (`id`, `author_id`, `title`, `description`, `content`, `date`) VALUES (3, 3, 'Adipisci officia sunt ut dolorem tempore.', 'Autem dolor consequatur nobis. Maxime laudantium ea sed modi quidem temporibus placeat. Voluptatum dolorem cupiditate distinctio est neque illo vitae. Officiis atque et debitis omnis. Accusamus aut harum omnis repellendus saepe facere voluptate.', 'Vero facere tempora et. Non quos accusantium et doloremque molestiae repellat. Et est repellendus odio necessitatibus ad ea.', '2007-01-03');
INSERT INTO `posts` (`id`, `author_id`, `title`, `description`, `content`, `date`) VALUES (4, 4, 'Quo harum nostrum illum ut.', 'Voluptatem omnis aliquid libero deleniti. Magni nihil ea et ut hic et ab. Non qui aut voluptatem nostrum aperiam. Et officia et ipsum magnam quia sint.', 'Et voluptatem velit adipisci aut deserunt. Et beatae aliquid qui sunt ut repellendus. Sit ut dolore unde aperiam magnam quia. Ea voluptatem accusantium tempore sit quia.', '1996-03-04');
